// testbench
auto
TestHw(param::src_port* src, stream::port* dst) noexcept -> void {
    HVX_INTERFACE_STREAM_NO_CTRL_TLP(src, dst);
    auto dst_fifo = hvx::HwFifo<param::dst_vec, param::dst_dim::vec_elms, 2>();
    hvx::HwConv<param>(src, fix_wgts, fix_bias, dst_fifo.data); // NOLINT
    hvx::HwHvxToStream<stream>(dst_fifo.data, *dst);
}

// input data generator for Vivado simulation testing
auto
TestHwSrcGenerator(param::src_vec* dst) {
    HVX_INTERFACE_STREAM_NO_CTRL(dst);
    hvx::HwSrcGenerator<param::src_vec, param::lat, 64, param::src_dim::vec_elms>(fix_src, dst); // NOLINT
}

// test bench
auto
main() -> int {
    hvx::conv_eval<param, hvx::eval_param<true, 4, 4, 4, stream::port, stream::flags>> eval(fix_src, fix_wgts, fix_bias); // NOLINT
    hvx::SwMeasureFuncTime("HW", 4, TestHw, fix_src, eval.GetDstHw());                                                    // NOLINT
    eval.Compute();
    return 0;
}
